Method.Implementation:
if (action = "") { set action = ..ACTIONMESSAGE }

// using CTWPv2
do ..Write(action)
do ..Write(query)
do ..Write(..ACTIONTERMINATOR)

if (query = "") quit $$$OK
#dim exception as %Exception.AbstractException do ..Write(..ACTIONMESSAGE) USE $Principal:(/SEN=1) USE $Principal:(::"+Q")
set value = $Replace(query,$CHAR(10)," ")
set error = "" // clear error
try {
xecute value // call a new stack
} catch exception {
write exception.DisplayString()
}
use $Principal:(/SEN=0) use $Principal:(::"-Q") do ..Write(..ACTIONTERMINATOR)
if (..CurrentNamespace '= $Znspace) { // change client namespace set ..CurrentNamespace = $Znspace do ..SendData(..CurrentNamespace,..ACTIONCHANGENAMESPACE) }
quit $$$OK

set key = ..Read(,.status) // wait for package set realKey = ..GetAuthKey() // remember current auth key do ..GenerateAuthKey() // generate new auth key
if (key = realKey) { quit $$$OK }
quit $$$NOTOK


	for {

		set action = ..Read(,.status)

    	if $$$ISOK(status) {

	    	if (action = ..ACTIONMESSAGE) { // autorized

	    		//USE $Principal:(/SEN=1)
   				//USE $Principal:(::"+Q")
	    		set data = ..Read(,.status)

		    	do ..ExecuteCommand(data)
		    	//do ..SendData(..ACTIONEXECUTED,messageId) // alert client

	    	} elseif (action = ..ACTIONSQL) { // sql

		    	//set pos = $Find(data,..ACTIONMESSAGE,1) // find first separator
	    		//set messageId = $Extract(data,1,pos-2)
	    		//set message = $Extract(data,pos,)
	    		set data = ..Read(,.status)

		    	do ..ExecuteSQL(data)

	    	} elseif (action = ..ACTIONTERMINATOR) {

		    	// fu

	    	} else { // something scary

		    	do ..SendData(..ACTIONMESSAGE,"Unrecognised query.")

	    	}

    	} else { // $$$ISOK(status)

	    	do ..SendData("Not ok!",..ACTIONMESSAGE)
      		quit:($$$GETERRORCODE(status)=$$$CSPWebSocketClosed)

    	}
	}

	set ..lastClientId = ..lastClientId + 1

	if ($$$ISOK(..RequireAuthorization())) {
		do ..SendData("Authorization succeeded. Execution permitted.")

		set ..CurrentNamespace = $Znspace
		do ..SendData(..CurrentNamespace,..ACTIONCHANGENAMESPACE)

		do ..ClientLoop(..lastClientId)
	} else {
		do ..SendData("Wrong authorization key")
		do ..EndServer()	
	}

	do ..SendData("Good bye!",..ACTIONMESSAGE)

  	quit $$$OK
